package org.collection;

import java.util.ArrayList;

public class List {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<Integer> li= new ArrayList<Integer>();
		li.add(10);
		li.add(20);
		li.add(30);
		for(int i=0;i<li.size();i++) {
		System.out.println(i);
		}
	}

}
